mapper.update:
update users
set balance = balance + #{balance,jdbcType=INTEGER}
where name = #{name,jdbcType=VARCHAR}

update users
set password = #{balance,jdbcType=INTEGER}
where name = #{name,jdbcType=VARCHAR}

update users
set avatar = #{avatar,jdbcType=VARCHAR}
where name = #{name,jdbcType=VARCHAR}

mapper.select:
select avatar
from users
where name = #{name,jdbcType=VARCHAR}
